import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { Axios } from "axios";

const initialState = {
  value: [],
  loading: false,
  error: "",
};

export const getData = createAsyncThunk("getData", async (dispatch) => {
  const response = await Axios.get("http://localhost:3001/getpersonels");
  dispatch(getData(response.data));
});

export const userSlice = createSlice({
  name: "Users",
  initialState,
  reducers: {
    addUser: (state, action) => {
      state.value.push(action.payload);
    },
    getPersonel: (state, action) => {
      state.value = [action.payload];
    },
    deleteUser: (state, action) => {
      state.value = state.value.filter((user) => user.id !== action.payload.id);
    },
    updateUser: (state, action) => {
      state.value.map((user) => {
        if (user.id === action.payload.id) {
          user.name = action.payload.name;
        }
      });
    },
  },
  extraReducers: (builder) => {
    builder.addCase(getData.pending, (state, action) => {
      state.loading = true;
      state.error = "";
    });
    builder.addCase(getData.fulfilled, (state, action) => {
      state.value = action.payload;
      state.loading = false;
    });
    builder.addCase(getData.rejected, (state, action) => {
      state.loading = false;
      state.error = "error data";
    });
  },
});

export const { addUser, deleteUser, updateUser, getPersonel } =
  userSlice.actions;
export default userSlice.reducer;
