import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

import axios, { Axios } from "axios";

export const getData = createAsyncThunk("getData", async () => {
  try {
    const response = await axios.get("http://localhost:3001/getpersonels");

    return response.data;
  } catch (error) {
    console.error(error);
  }
});

export const addDataAndGet = createAsyncThunk(
  "data/update",
  async ({ name, surname }) => {
    addData({ name, surname });
    const { data } = getData();
    return data;
  }
);

export const addData = createAsyncThunk(
  "createData",
  async ({ name, surname }, dispatch) => {
    try {
      const response = await axios.post("http://localhost:3001/create", {
        name: name,
        surname: surname,
      });
      return response.data;
    } catch (error) {
      console.error(error);
    }
  }
);

export const updateData = createAsyncThunk("upData", async ({ id, name }) => {
  try {
    const response = await axios.put(
      `http://localhost:3001/updatepersonels/${id}`,
      {
        name: name,
      }
    );
    return response.data;
  } catch (error) {
    console.error(error);
  }
});

export const userSlice = createSlice({
  name: "users",
  initialState: {
    users: [],
    loading: false,
    error: "",
  },
  reducers: {},
  extraReducers: (builder) => {
    builder.addCase(getData.pending, (state, action) => {
      state.loading = true;
      state.error = "";
    });
    builder.addCase(getData.fulfilled, (state, action) => {
      state.users = action.payload;
      state.loading = false;
    });
    builder.addCase(getData.rejected, (state, action) => {
      state.loading = false;
      state.error = action.error.message;
    });

    builder.addCase(addData.pending, (state, action) => {
      state.loading = true;
      state.error = "";
    });
    builder.addCase(addData.fulfilled, (state, action) => {
      state.users = [...action.payload];
      state.loading = false;
    });
    builder.addCase(addData.rejected, (state, action) => {
      state.loading = false;
      state.error = action.error.message;
    });

    builder.addCase(updateData.pending, (state, action) => {
      state.loading = true;
      state.error = "";
    });
    builder.addCase(updateData.fulfilled, (state, action) => {
      state.users = [...action.payload];
      state.loading = false;
    });
    builder.addCase(updateData.rejected, (state, action) => {
      state.loading = false;
      state.error = action.error.message;
    });
  },
});

export const selectPersonel = (state) => state.users.value;
export const { addUser, deleteUser, updateUser } = userSlice.actions;
export default userSlice.reducer;
